import React, { useContext } from 'react';
import { Route, Routes , Navigate } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Perfil from './pages/Perfil';
import Test from './pages/Test';
import Chats from './pages/Chats';
import { useAuth } from "./Hooks/UseAuth";
import { HomeLayout } from './Components/HomeLayout';
import 'bootstrap/dist/css/bootstrap.min.css';
import swal from 'sweetalert2';
import { AuthConext } from './Context/AuthContext';
import CryptoJS from 'crypto-js';






function App() {

  //Manejar estado de encriptacion
    const [encriptar, setEncriptar] = useState(false);

    const toggleEncrypt = () => {
        setEncriptar(!encriptar)
        console.log(encriptar)
    }

    //Funciones para encriptar los mensajes
    const cifrar = (texto) => {
        console.log("AQUIIII")
        if (encriptar) {
            var textoCifrado = CryptoJS.AES.encrypt(texto, "poi").toString();
            console.log("se encripto")
            return textoCifrado;
        } else {
            console.log("No se encripto")
            return texto;
        }
    }

    const descifrar = (texto) => {
        var bytes = CryptoJS.AES.decrypt(texto, "poi");
        var textoDescifrado = bytes.toString(CryptoJS.enc.Utf8);

        if (textoDescifrado) {
            return textoDescifrado;
        } else {
            return texto;
        }


    }


  const {currentUser} = useContext(AuthConext);
  console.log(currentUser);

  const ProtectedRoute = ({ children }) => {
    if (!currentUser) {
      return <Navigate to="/login" />;
    }

    return children
  };

  const mostrarAlerta=()=>{
    swal('Esta cosa pa');
  }

  const { user } = useAuth();
  const funcionPrueba = ()=>{
    console.dir("Fuera del componente");
  }
  return (
    <Routes>
      <Route path="/" element={<Login />}/>
      <Route path="/login" element={<Login />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/test" element={<Test />} />
      <Route path="/Chats" element={<ProtectedRoute><Chats /></ProtectedRoute>} />

      <Route path="/home" element={<ProtectedRoute><HomeLayout login={user}/></ProtectedRoute>}>
        <Route path="start" element={<Home login={user}/>} />
        <Route path="perfil" element={<Perfil login={user} funcion={funcionPrueba}/>} />
        <Route path="settings" element={<Home login={user}/>} />
      </Route>

      <Route
        path="*"
        element={<Navigate to="/" />}
      />
        </Routes>
  );
}



export default App;
